// Get config from Extract & Configure node
const config = $('Extract & Configure').first().json;

// Get review rules from Load Review Rules node (if available)
const reviewRules = $('Load Review Rules from GitLab').first()?.json?.data || '';

// Get MR diff data
const mrData = $input.item.json;

const projectName = config.projectName;
const mrTitle = config.mrTitle;
const mrDescription = config.mrDescription;
const sourceBranch = config.sourceBranch;
const targetBranch = config.targetBranch;
const webUrl = config.mrUrl;
const changes = mrData.changes || [];

// Build diff text from changes
let diffText = '';
let totalSize = 0;
const MAX_CHUNK_SIZE = 100000; // chars per chunk (adjust based on LLM limits)

const fileChanges = [];

for (const change of changes) {
  const filePath = change.new_path || change.old_path;
  const diff = change.diff || '';

  // Skip binary files
  if (change.binary) {
    continue;
  }

  fileChanges.push({
    path: filePath,
    diff: diff,
    new_file: change.new_file,
    deleted_file: change.deleted_file,
    renamed_file: change.renamed_file
  });

  diffText += `\n\n### File: ${filePath}\n`;
  if (change.new_file) diffText += '[NEW FILE]\n';
  if (change.deleted_file) diffText += '[DELETED FILE]\n';
  if (change.renamed_file) diffText += `[RENAMED from ${change.old_path}]\n`;
  diffText += '```diff\n' + diff + '\n```\n';

  totalSize += diff.length;
}

// Split into chunks if too large
const chunks = [];
if (totalSize > MAX_CHUNK_SIZE) {
  let currentChunk = '';
  let currentFiles = [];

  for (const fileChange of fileChanges) {
    const fileSection = `\n\n### File: ${fileChange.path}\n` +
      (fileChange.new_file ? '[NEW FILE]\n' : '') +
      (fileChange.deleted_file ? '[DELETED FILE]\n' : '') +
      (fileChange.renamed_file ? `[RENAMED from ${fileChange.old_path}]\n` : '') +
      '```diff\n' + fileChange.diff + '\n```\n';

    if (currentChunk.length + fileSection.length > MAX_CHUNK_SIZE && currentChunk.length > 0) {
      chunks.push({
        text: currentChunk,
        files: [...currentFiles]
      });
      currentChunk = fileSection;
      currentFiles = [fileChange.path];
    } else {
      currentChunk += fileSection;
      currentFiles.push(fileChange.path);
    }
  }

  if (currentChunk.length > 0) {
    chunks.push({
      text: currentChunk,
      files: currentFiles
    });
  }
} else {
  chunks.push({
    text: diffText,
    files: fileChanges.map(f => f.path)
  });
}

// Prepare review prompt with custom rules
let reviewPrompt = `You are an expert code reviewer. Please review the following merge request changes and provide constructive feedback based on the provided review guidelines.

**Project:** ${projectName}
**MR Title:** ${mrTitle}
**Description:** ${mrDescription}
**Branch:** ${sourceBranch} â†’ ${targetBranch}
**URL:** ${webUrl}

`;

// Add custom review rules if available
if (reviewRules && reviewRules.trim().length > 0) {
  reviewPrompt += `**Code Review Guidelines:**

Please follow these specific review rules and guidelines:

${reviewRules}

---

`;
} else {
  // Fallback to basic instructions if no custom rules are loaded
  reviewPrompt += `**Review Instructions:**
1. Review the code changes for:
   - Bugs and potential issues
   - Security vulnerabilities (SQL injection, XSS, authentication issues, etc.)
   - Performance concerns (inefficient algorithms, N+1 queries, etc.)
   - Code quality and best practices
   - Maintainability and readability
2. Provide specific, actionable feedback with line numbers
3. Highlight both issues and good practices
4. Use severity levels: ðŸ”´ CRITICAL, ðŸŸ¡ IMPORTANT, ðŸŸ¢ SUGGESTION
5. Format your response as a structured review with sections

`;
}

reviewPrompt += `**Code Changes:**
`;

return chunks.map((chunk, index) => ({
  json: {
    projectId: config.projectId,
    mrIid: config.mrIid,
    mrTitle: mrTitle,
    mrUrl: webUrl,
    gitlabUrl: config.gitlabUrl,
    chunkIndex: index,
    totalChunks: chunks.length,
    files: chunk.files,
    prompt: reviewPrompt + chunk.text,
    diffText: chunk.text
  }
}));

// Combine all chunk reviews if multiple chunks
const items = $input.all();

if (items.length === 0) {
  return [];
}

// Group by MR
const mrGroups = {};
for (const item of items) {
  const mrIid = item.json.mrIid;
  if (!mrGroups[mrIid]) {
    mrGroups[mrIid] = [];
  }
  mrGroups[mrIid].push(item);
}

const results = [];

for (const [mrIid, mrItems] of Object.entries(mrGroups)) {
  const firstItem = mrItems[0].json;

  let combinedReview = '';

  if (mrItems.length > 1) {
    combinedReview = `## Code Review Summary\n\nThis merge request was reviewed in ${mrItems.length} parts due to its size.\n\n`;

    mrItems.sort((a, b) => a.json.chunkIndex - b.json.chunkIndex);

    for (const item of mrItems) {
      const review = item.json.response?.output || item.json.output || 'No review generated';
      combinedReview += `\n### Part ${item.json.chunkIndex + 1} - Files: ${item.json.files.join(', ')}\n\n`;
      combinedReview += review + '\n\n---\n\n';
    }
  } else {
    combinedReview = mrItems[0].json.response?.output || mrItems[0].json.output || 'No review generated';
  }

  // Add footer with review metadata
  combinedReview += `\n\n---\n*ðŸ¤– Automated code review powered by AI | Generated on ${new Date().toISOString()}*`;

  results.push({
    json: {
      projectId: firstItem.projectId,
      mrIid: mrIid,
      mrUrl: firstItem.mrUrl,
      gitlabUrl: firstItem.gitlabUrl,
      review: combinedReview,
      timestamp: new Date().toISOString()
    }
  });
}

return results;
